import React from 'react'
import { formatPrice } from '../helpers'

class EditFishForm extends React.Component {
  render() {
    return (
     <edit className="fish-edit">
       <input name='name'  ONvalue={this.props.fish.name}/>
       <input type='text' name='price'   ONvalue={formatPrice(this.props.fish.price)}/>
       <select type='text' name='status'  ONvalue={this.props.fish.status} >
         <option value="available">Fresh!</option>
         <option value="unavailable">Sold Out!</option>
       </select>
       <textarea name='desc' ONvalue={this.props.fish.desc}></textarea>
       <input name='image'  ONvalue={this.props.fish.image}/>
     </edit>
    )
  }
}

export default EditFishForm
